    private void ogretmengirisbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ogretmengirisbtnActionPerformed

        Connection c = bglnt.dbcon();
        Statement st = null;
        ResultSet rs = null;
        ResultSet rs1 = null;
        boolean flag = false;
        if (ogretmenidtxt.getText().length() == 0 || ogretmensifretxt.getText().length() == 0) {
            JOptionPane.showMessageDialog(null, "L?tfen Bo? Alanlar? Doldurunuz!");
            ogretmenidtxt.setText("");
            ogretmensifretxt.setText("");
        } else {

            try {
                st = c.createStatement();
                rs = st.executeQuery("select * from \"Hocalar\"");
                rs1 = bilgial("select * from \"Sistem\" ");
                while (rs.next()) {
                    if ((ogretmenidtxt.getText().equalsIgnoreCase(rs.getString("sicilNo"))) && (ogretmensifretxt.getText().equalsIgnoreCase(rs.getString("hSifre")))) {
                        flag = true;
                        Ogretmen ogretmen = new Ogretmen();
                        ogretmen.setSicilNo(rs.getInt("sicilNo"));
                        ogretmen.sethSifre(rs.getString("hSifre"));

                        ogretmenidtxt.setText("");
                        ogretmensifretxt.setText("");

                        hocaFrame hocaFrame = new hocaFrame();
                        hocaFrame.bilgiyaz(ogretmen);

                        while (rs1.next()) {

                            if (rs1.getBoolean("asama") == false) {
                                hocaFrame.tablobilgigor(hocaFrame.hocataleptable);
                                hocaFrame.kritersilbtn.setEnabled(true);
                                hocaFrame.kriterbtn.setEnabled(true);
                                hocaFrame.ilgibtn.setEnabled(true);
                            } else {

                                hocaFrame.kritersilbtn.setEnabled(false);
                                hocaFrame.kriterbtn.setEnabled(false);
                                hocaFrame.ilgibtn.setEnabled(false);
                            }

                        }

                        hocaFrame.dersler(ogretmen);

                        dispose();
                        hocaFrame.setVisible(true);

                    } else {

                    }
                }

                if (flag == false) {
                    JOptionPane.showMessageDialog(null, "Kullanici bilgileri yanlis");
                    ogretmenidtxt.setText("");
                    ogretmensifretxt.setText("");
                }

            } catch (SQLException ex) {
                Logger.getLogger(menu.class.getName()).log(Level.SEVERE, null, ex);
            } finally {
                try {
                    if (rs != null) {
                        rs.close();
                    }
                    if (rs1 != null) {
                        rs1.close();
                    }
                    if (st != null) {
                        st.close();
                    }
                    if (c != null) {
                        c.close();
                    }
                } catch (SQLException ex) {
                    Logger.getLogger(menu.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
    }//GEN-LAST:event_ogretmengirisbtnActionPerformed

