    public ResultSet bilgial(String query) {
        Connection c = bglnt.dbcon();
        Statement st = null;
        ResultSet rs = null;

        try {
            st = c.createStatement();
            rs = st.executeQuery(query);
            return rs;
        } catch (SQLException ex) {
            Logger.getLogger(yoneticiFrame.class.getName()).log(Level.SEVERE, null, ex);
            return null;
        } finally {
            try {
                // rs.close(); 
                st.close();
                c.close();
            } catch (SQLException ex) {
                Logger.getLogger(hocaFrame.class.getName()).log(Level.SEVERE, null, ex);
            }

        }

    }

    private void ogrencigirisbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ogrencigirisbtnActionPerformed

        Connection c = bglnt.dbcon();
        Statement st = null;
        ResultSet rs = null;
        ResultSet rs1 = null;
        boolean flag = false;
        if (ogrenciidtxt.getText().length() == 0 || ogrencisifretxt.getText().length() == 0) {
            JOptionPane.showMessageDialog(null, "L?tfen Bo? Alanlar? Doldurunuz!");
            ogrenciidtxt.setText("");
            ogrencisifretxt.setText("");
        } else {

            try {
                st = c.createStatement();
                rs = st.executeQuery("select * from \"Ogrenciler\"");
                rs1 = bilgial("select * from \"Sistem\" ");
                while (rs.next()) {
                    if ((ogrenciidtxt.getText().equalsIgnoreCase(rs.getString("ogrNo"))) && (ogrencisifretxt.getText().equalsIgnoreCase(rs.getString("ogrSifre")))) {
                        flag = true;

                        Ogrenci ogrenci = new Ogrenci();
                        ogrenci.setOgrNo(rs.getInt("ogrNo"));
                        ogrenci.setOgrSifre(rs.getString("ogrSifre"));
                        ogrenci.setGNO(rs.getDouble("GNO"));
                        ogrenci.setOgrAd(rs.getString("ogrAd"));
                        ogrenci.setOgrSoyad(rs.getString("ogrSoyad"));
                        ogrenci.setToplamTalep(rs.getInt("toplamTalep"));
                        ogrenci.setTalepOnay(rs.getInt("talepOnay"));

                        ogrenciidtxt.setText("");
                        ogrencisifretxt.setText("");

                        ogrFrame ogrFrame = new ogrFrame();
                        ogrFrame.bilgiyaz(ogrenci);

                        while (rs1.next()) {

                            if (rs1.getBoolean("asama") == false) {

                                ogrFrame.tablotalep(ogrFrame.derssecimtable);
                                ogrFrame.tablo(ogrFrame.ogrtaleplertable);
                                ogrFrame.tablotalep(ogrFrame.ilgihocatable);
                                ogrFrame.belgebtn.setEnabled(true);
                                if (ogrFrame.ogrenci.getDersad() != null) {
                                    ogrFrame.filtrebtn.setEnabled(true);
                                } else {
                                    ogrFrame.filtrebtn.setEnabled(false);
                                }

                            } else {
                                ogrFrame.belgebtn.setEnabled(false);

                                ogrFrame.derssecbtn.setEnabled(false);
                                ogrFrame.filtrebtn.setEnabled(false);

                            }

                        }

                        dispose();
                        ogrFrame.setVisible(true);

                    } else {

                    }
                }
                if (flag == false) {
                    JOptionPane.showMessageDialog(null, "Kullanici bilgileri yanlis");
                    ogrenciidtxt.setText("");
                    ogrencisifretxt.setText("");
                }

            } catch (SQLException ex) {
                Logger.getLogger(menu.class.getName()).log(Level.SEVERE, null, ex);
            } finally {
                try {
                    if (rs != null) {
                        rs.close();
                    }
                    if (st != null) {
                        st.close();
                    }
                    if (c != null) {
                        c.close();
                    }
                } catch (SQLException ex) {
                    Logger.getLogger(menu.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }

    }//GEN-LAST:event_ogrencigirisbtnActionPerformed

